mapper.update:
UPDATE TRANSACTION
PAYMENT_OPTION = #{paymentOption:VARCHAR}, RECEIVER_NAME = #{receiverName:VARCHAR}, RECEIVER_PHONE = #{receiverPhone:VARCHAR}, DLVY_ADDR = #{divyAddr:VARCHAR}, DLVY_REQUEST = #{divyRequest:VARCHAR}, DLVY_DATE = #{divyDate:VARCHAR}
WHERE TRAN_NO = #{tranNo}

mapper.select:
  	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  					FROM		(	SELECT * 
										FROM TRANSACTION 
											
												BUYER_ID = #{buyerId} 
												
													
														AND TRAN_STATUS_CODE = #{search.searchKeyword} 
													
													
											
											ORDER BY TRAN_NO
										
										 ) inner_table
					WHERE ROWNUM <= #{search.endRowNum} )
	WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

  	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  					FROM		(	SELECT * 
										FROM TRANSACTION 
											
											
												
									 				BUYER_ID LIKE '%' || #{searchKeyword} || '%' 
												
												
									 				PAYMENT_OPTION = #{searchKeyword} 
												
												
									 				TRAN_STATUS_CODE = #{searchKeyword} 
												
											
										 
					) inner_table
					WHERE ROWNUM <= #{endRowNum} )
	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*) FROM(	SELECT * FROM TRANSACTION 
						
							BUYER_ID = #{buyerId} 
							
								
				 					AND TRAN_STATUS_CODE = #{search.searchKeyword} 
								
							
						
					 ) countTable

  	SELECT COUNT(*) FROM(	SELECT * FROM TRANSACTION 
					
						
							
				 				BUYER_ID LIKE '%' || #{searchKeyword} || '%' 
							
							
				 				PAYMENT_OPTION = #{searchKeyword} 
							
							
				 				TRAN_STATUS_CODE = #{searchKeyword} 
							
						
					 ) countTable
